import React, { useState } from "react";
import "./FoodDonation.css";

function FoodDonation() {
  const [formData, setFormData] = useState({
    foodName: "",
    quantity: "",
    quality: "",
    bestBefore: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("‚úÖ Food donation submitted successfully!");
  };

  return (
    <div className="food-donation-container">
      <h2>üç± Food Donation Form</h2>
      <form onSubmit={handleSubmit}>
        <label>Food Name</label>
        <input name="foodName" onChange={handleChange} required />

        <label>Quantity</label>
        <input name="quantity" onChange={handleChange} required />

        <label>Quality</label>
        <input name="quality" onChange={handleChange} required />

        <label>Best Before</label>
        <input name="bestBefore" onChange={handleChange} required />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FoodDonation;
