import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix default marker issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png",
});

const FoodTracking = ({ type }) => {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    // Mock nearest locations based on donation type
    let data = [];
    if (type === "Ready-made Packed") {
      data = [
        { id: 1, name: "HelpingHand NGO", lat: 19.076, lng: 72.8777, quantity: "20 kg" },
        { id: 2, name: "City Pantry", lat: 18.5204, lng: 73.8567, quantity: "15 kg" },
      ];
    } else if (type === "Uncooked (Grains, Pulses, Oil)") {
      data = [
        { id: 3, name: "Grain Support NGO", lat: 28.6139, lng: 77.209, quantity: "50 kg" },
      ];
    } else {
      data = [
        { id: 4, name: "Hotel Pickup", lat: 12.9716, lng: 77.5946, quantity: "10 kg" },
      ];
    }
    setLocations(data);
  }, [type]);

  return (
    <div style={{ height: "400px", width: "100%", marginTop: "20px" }}>
      <MapContainer center={[20.5937, 78.9629]} zoom={5} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {locations.map((loc) => (
          <Marker key={loc.id} position={[loc.lat, loc.lng]}>
            <Popup>
              <strong>{loc.name}</strong>
              <br />
              Quantity: {loc.quantity}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default FoodTracking;
