import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Welcome.css";

function Welcome() {
  const navigate = useNavigate();
  const [quoteIndex, setQuoteIndex] = useState(0);
  const [welcomeIndex, setWelcomeIndex] = useState(0);

  const quotes = [
    "‚ÄúEducation is the most powerful weapon which you can use to change the world.‚Äù ‚Äî Nelson Mandela",
    "‚ÄúEvery child deserves a happy and healthy childhood.‚Äù ‚Äî Anonymous",
    "‚ÄúAn investment in knowledge pays the best interest.‚Äù ‚Äî Benjamin Franklin",
    "‚ÄúHelping one person might not change the world, but it can change the world for one person.‚Äù"
  ];

  const welcomeLanguages = [
    "Welcome", "‡§∏‡•ç‡§µ‡§æ‡§ó‡§§ ‡§π‡•à", "‡Æµ‡Ææ‡Æ¥‡Øç‡Æ§‡Øç‡Æ§‡ØÅ‡Æï‡Æ≥‡Øç", "‡≤∏‡≥ç‡≤µ‡≤æ‡≤ó‡≤§", "‡¥∏‡µç‡¥µ‡¥æ‡¥ó‡¥§‡¥Ç", "‡™∏‡´ç‡™µ‡™æ‡™ó‡™§ ‡™õ‡´á",
    "‡¶∏‡ßç‡¶¨‡¶æ‡¶ó‡¶§‡¶Æ", "‡∞∏‡±ç‡∞µ‡∞æ‡∞ó‡∞§‡∞Ç", "‡Æµ‡Æ£‡Æï‡Øç‡Æï‡ÆÆ‡Øç", "‡§®‡§Æ‡§∏‡•ç‡§§‡•á", "‡∞∂‡±Å‡∞≠‡∞æ‡∞ó‡∞§‡∞Ç", "·û¢·ûö·ûª·ûé·ûü·ûΩ·ûü·üí·ûè·û∏"
  ];

  useEffect(() => {
    const quoteInterval = setInterval(() => {
      setQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
    }, 5000);

    const welcomeInterval = setInterval(() => {
      setWelcomeIndex((prevIndex) => (prevIndex + 1) % welcomeLanguages.length);
    }, 3000);

    return () => {
      clearInterval(quoteInterval);
      clearInterval(welcomeInterval);
    };
  }, []);

  const enterSite = () => {
    navigate("/home");
  };

  const emojis = ["üåé", "‚ù§Ô∏è", "üçé", "üìö", "üéÅ", "ü©∫", "üß∏"];

  return (
    <div className="welcome-container">
      {/* Top banner showing one welcome at a time */}
      <div className="welcome-banner">
        <span className="welcome-word">{welcomeLanguages[welcomeIndex]}</span>
      </div>

      {/* Floating emojis */}
      <div className="emoji-container">
        {emojis.map((emoji, i) => (
          <span
            key={i}
            className="floating-emoji"
            style={{
              left: `${Math.random() * 100}%`,
              animationDuration: `${5 + Math.random() * 5}s`
            }}
          >
            {emoji}
          </span>
        ))}
      </div>

      <h1 className="ngo-title">Helping Universe NGO</h1>
      <p className="quote">{quotes[quoteIndex]}</p>
      <button className="enter-button" onClick={enterSite}>Enter Site</button>
    </div>
  );
}

export default Welcome;
