import React, { useState } from "react";
import jsPDF from "jspdf";
import "./MoneyDonation.css";

const MoneyDonation = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    amount: "",
    paymentMethod: "UPI",
  });
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const generateReceipt = () => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("Helping Universe NGO", 70, 20);
    doc.setFontSize(14);
    doc.text("Donation Receipt", 85, 35);
    doc.line(20, 40, 190, 40);
    doc.setFontSize(12);
    doc.text(`Name: ${formData.name}`, 20, 55);
    doc.text(`Email: ${formData.email}`, 20, 65);
    doc.text(`Amount Donated: â‚¹${formData.amount}`, 20, 75);
    doc.text(`Payment Method: ${formData.paymentMethod}`, 20, 85);
    doc.text(`Date: ${new Date().toLocaleString()}`, 20, 95);
    doc.line(20, 100, 190, 100);
    doc.text("Thank you for supporting our cause ðŸ’–", 40, 115);
    doc.save("Donation_Receipt.pdf");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name || !formData.amount) {
      alert("Please fill all fields");
      return;
    }
    setSuccess(true);
    generateReceipt();
  };

  return (
    <div className="money-donation-container">
      <h2>ðŸ’° Money Donation</h2>
      <p>Your small help can bring a big change!</p>

      {!success ? (
        <form onSubmit={handleSubmit} className="donation-form">
          <input
            type="text"
            name="name"
            placeholder="Enter your name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="amount"
            placeholder="Enter amount (â‚¹)"
            value={formData.amount}
            onChange={handleChange}
            required
          />

          <select
            name="paymentMethod"
            value={formData.paymentMethod}
            onChange={handleChange}
          >
            <option value="UPI">UPI</option>
            <option value="Credit Card">Credit Card</option>
            <option value="Debit Card">Debit Card</option>
            <option value="Net Banking">Net Banking</option>
          </select>

          <button type="submit">Donate Now ðŸ’–</button>
        </form>
      ) : (
        <div className="success-message">
          <h3>ðŸŽ‰ Thank you for your donation!</h3>
          <p>Your receipt has been downloaded successfully.</p>
        </div>
      )}
    </div>
  );
};

export default MoneyDonation;
