import React, { useState } from "react";
import FoodTracking from "../components/FoodTracking";
import "./FoodDonation.css"; // Add your styles

const FoodDonation = () => {
  const [donationType, setDonationType] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const [formData, setFormData] = useState({
    ngoName: "",
    foodName: "",
    quantity: "",
    type: "",
    bestBefore: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleTypeSelect = (type) => {
    setDonationType(type);
    setSubmitted(false);
    setFormData({
      ngoName: "",
      foodName: "",
      quantity: "",
      type: type,
      bestBefore: "",
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation
    if (!formData.ngoName || !formData.foodName || !formData.quantity) {
      alert("Please fill all required fields.");
      return;
    }

    if (isNaN(formData.quantity) || Number(formData.quantity) <= 0) {
      alert("Quantity must be a positive number (kg).");
      return;
    }

    setSubmitted(true);
    console.log("Food Donation Submitted:", formData);
  };

  return (
    <div className="food-donation-container">
      <h2>Food Donation</h2>
      <div className="donation-type-buttons">
        <button onClick={() => handleTypeSelect("Ready-made Packed")}>
          Ready-made Packed
        </button>
        <button onClick={() => handleTypeSelect("Uncooked (Grains, Pulses, Oil)")}>
          Uncooked
        </button>
        <button onClick={() => handleTypeSelect("Remaining Food (Hotels/Functions)")}>
          Remaining Food
        </button>
      </div>

      {donationType && !submitted && (
        <form className="food-donation-form" onSubmit={handleSubmit}>
          <h3>{donationType} Donation Form</h3>
          <label>
            NGO Name:
            <input
              type="text"
              name="ngoName"
              value={formData.ngoName}
              onChange={handleChange}
              placeholder="Your NGO Name"
              required
            />
          </label>
          <label>
            Food Name:
            <input
              type="text"
              name="foodName"
              value={formData.foodName}
              onChange={handleChange}
              placeholder="Food Item Name"
              required
            />
          </label>
          <label>
            Quantity (kg):
            <input
              type="number"
              name="quantity"
              value={formData.quantity}
              onChange={handleChange}
              placeholder="Quantity in kg"
              min="1"
              required
            />
          </label>
          <label>
            Best Before:
            <input
              type="date"
              name="bestBefore"
              value={formData.bestBefore}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit">Submit Donation</button>
        </form>
      )}

      {submitted && (
        <div className="tracking-section">
          <h3>Track Your Food Donation</h3>
          <FoodTracking />
        </div>
      )}
    </div>
  );
};

export default FoodDonation;
