import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Welcome() {
  const navigate = useNavigate();
  const [quoteIndex, setQuoteIndex] = useState(0);

  const quotes = [
    "“Education is the most powerful weapon which you can use to change the world.” — Nelson Mandela",
    "“Every child deserves a happy and healthy childhood.” — Anonymous",
    "“An investment in knowledge pays the best interest.” — Benjamin Franklin",
    "“Helping one person might not change the world, but it can change the world for one person.”"
  ];

  // Cycle through quotes every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const enterSite = () => {
    navigate("/home");
  };

  return (
    <div style={{
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      height: "100vh",
      background: "linear-gradient(to right, #00c6ff, #0072ff)",
      color: "white",
      textAlign: "center",
      fontFamily: "Arial, sans-serif",
      padding: "20px"
    }}>
      <h1 style={{ fontSize: "3rem", marginBottom: "30px" }}>
        Helping Universe NGO
      </h1>
      <p style={{ fontSize: "1.5rem", fontStyle: "italic", marginBottom: "40px", maxWidth: "800px" }}>
        {quotes[quoteIndex]}
      </p>
      <button
        onClick={enterSite}
        style={{
          padding: "12px 30px",
          borderRadius: "25px",
          backgroundColor: "#ff6b6b",
          color: "white",
          border: "none",
          fontSize: "1rem",
          cursor: "pointer",
          boxShadow: "0 4px 6px rgba(0,0,0,0.3)",
          transition: "transform 0.2s",
        }}
        onMouseOver={(e) => (e.target.style.transform = "scale(1.1)")}
        onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
      >
        Enter Site
      </button>
    </div>
  );
}

export default Welcome;
