import React from "react";
import "./Volunteers.css";

function Volunteers() {
  const volunteers = [
    {
      name: "Aarav Patel",
      role: "Food Distribution Lead",
      image: "/images/volunteer1.jpg",
      quote: "Happiness multiplies when shared with others.",
    },
    {
      name: "Priya Sharma",
      role: "Education Support Volunteer",
      image: "/images/volunteer2.jpg",
      quote: "Teaching one child can change a generation.",
    },
    {
      name: "Rohan Verma",
      role: "Health Awareness Coordinator",
      image: "/images/volunteer3.jpg",
      quote: "A healthy community is a happy community.",
    },
    {
      name: "Neha Joshi",
      role: "Clothes & Essentials Drive",
      image: "/images/volunteer4.jpg",
      quote: "Every small act of kindness makes a difference.",
    },
  ];

  return (
    <div className="volunteers-container">
      <header className="volunteers-header">
        <h1>üåü Our Dedicated Volunteers</h1>
        <p>
          Meet the hearts and hands behind <strong>Helping Universe NGO</strong>.
          They dedicate their time and passion to spreading hope and smiles.
        </p>
      </header>

      <div className="volunteer-grid">
        {volunteers.map((v, index) => (
          <div key={index} className="volunteer-card">
            <img src={v.image} alt={v.name} />
            <h2>{v.name}</h2>
            <h4>{v.role}</h4>
            <p>"{v.quote}"</p>
          </div>
        ))}
      </div>

      <section className="join-section">
        <h2>ü§ù Want to Join Us?</h2>
        <p>
          Become part of our journey to make the world a better place.
        </p>
        <button onClick={() => (window.location.href = "/register")}>
          Join as Volunteer
        </button>
      </section>
    </div>
  );
}

export default Volunteers;
