import React, { useState } from "react";
import "./FoodDonation.css";

const FoodDonation = () => {
  const [formData, setFormData] = useState({
    foodName: "",
    quantity: "",
    quality: "",
    bestBefore: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("‚úÖ Food donation submitted successfully!");
    console.log("Form Data:", formData);
  };

  return (
    <div className="food-donation-container">
      <h2>üç± Food Donation Form</h2>
      <form onSubmit={handleSubmit} className="food-donation-form">
        <label>Food Name</label>
        <input
          type="text"
          name="foodName"
          placeholder="Enter food name"
          value={formData.foodName}
          onChange={handleChange}
          required
        />

        <label>Quantity</label>
        <input
          type="text"
          name="quantity"
          placeholder="Enter quantity (e.g., 5 kg)"
          value={formData.quantity}
          onChange={handleChange}
          required
        />

        <label>Quality</label>
        <input
          type="text"
          name="quality"
          placeholder="Good / Average / Fresh"
          value={formData.quality}
          onChange={handleChange}
          required
        />

        <label>Best Before (date/time)</label>
        <input
          type="text"
          name="bestBefore"
          placeholder="e.g., Within 4 hours"
          value={formData.bestBefore}
          onChange={handleChange}
          required
        />

        <button type="submit" className="submit-btn">
          Submit Donation
        </button>
      </form>
    </div>
  );
};

export default FoodDonation;
