import React from "react";
import { FaAppleAlt, FaBook, FaTshirt, FaBox, FaMoneyBillWave } from "react-icons/fa";
import "./DonateOptions.css";
import { useNavigate } from "react-router-dom";

function DonateOptions() {
  const navigate = useNavigate();

  return (
    <div className="donate-options-container">
      <h2>Choose a Donation Type</h2>
      <div className="donate-options-buttons">
        <button className="donate-btn food" onClick={() => navigate("/food-donation")}>
          <FaAppleAlt size={50} />
          <span>Food Donation</span>
        </button>

        <button className="donate-btn books" onClick={() => navigate("/books-donation")}>
          <FaBook size={50} />
          <span>Books Donation</span>
        </button>

        <button className="donate-btn clothes" onClick={() => navigate("/clothes-donation")}>
          <FaTshirt size={50} />
          <span>Clothes Donation</span>
        </button>

        <button className="donate-btn old-things" onClick={() => navigate("/old-things-donation")}>
          <FaBox size={50} />
          <span>Old Things Donation</span>
        </button>

        <button className="donate-btn money" onClick={() => navigate("/money-donation")}>
          <FaMoneyBillWave size={50} />
          <span>Money Donation</span>
        </button>
      </div>
    </div>
  );
}

export default DonateOptions;
