import React, { useState } from "react";
import "./FoodDonation.css";
import { useNavigate } from "react-router-dom";

function FoodDonation() {
  const [selectedOption, setSelectedOption] = useState(null);
  const navigate = useNavigate();

  const options = [
    { id: "ready", label: "Ready-Made Packed Food" },
    { id: "uncooked", label: "Uncooked Food (Grains, Pulses, Oil etc)" },
    { id: "remaining", label: "Remaining Food (Nearby Hotels/Functions)" }
  ];

  return (
    <div className="food-donation-container">
      <h2>Food Donation Options</h2>
      <div className="food-options">
        {options.map(option => (
          <button
            key={option.id}
            className={`food-option-btn ${selectedOption === option.id ? "active" : ""}`}
            onClick={() => setSelectedOption(option.id)}
          >
            {option.label}
          </button>
        ))}
      </div>

      {selectedOption && (
        <div className="food-donation-form">
          <h3>Fill Donation Details</h3>
          <form>
            <label>Food Name:</label>
            <input type="text" placeholder="Enter food name" required />

            <label>Quantity:</label>
            <input type="text" placeholder="Enter quantity" required />

            <label>Quality:</label>
            <select required>
              <option value="">Select Quality</option>
              <option value="good">Good</option>
              <option value="average">Average</option>
            </select>

            <label>Best Before / Expiry:</label>
            <input type="date" required />

            <label>Additional Notes:</label>
            <textarea placeholder="Any other info..." />

            <button type="submit">Submit Donation</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default FoodDonation;
