import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix default marker issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png",
});

const FoodTracking = () => {
  const [locations, setLocations] = useState([
    // sample static locations
    { id: 1, name: "HelpingHand NGO", lat: 19.076, lng: 72.8777, quantity: "20 kg", type: "Remaining Food", bestBefore: "2025-10-27" },
    { id: 2, name: "Green Feast", lat: 28.6139, lng: 77.209, quantity: "15 kg", type: "Packed Food", bestBefore: "2025-10-28" },
  ]);

  return (
    <div style={{ height: "400px", width: "100%", marginTop: "20px" }}>
      <MapContainer center={[20.5937, 78.9629]} zoom={5} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />
        {locations.map((loc) => (
          <Marker key={loc.id} position={[loc.lat, loc.lng]}>
            <Popup>
              <strong>{loc.name}</strong>
              <br />
              Quantity: {loc.quantity}
              <br />
              Type: {loc.type}
              <br />
              Best Before: {loc.bestBefore}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default FoodTracking;
