import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar"; // ‚úÖ correct relative path
import "./Home.css";

function Home() {
  const slides = [
    "/images/food-donation.jpg",
    "/images/education.jpg",
    "/images/children-smile.jpg",
    "/images/medical-help.jpg",
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="home-container">
      {/* ‚úÖ Navbar must be inside JSX and above everything */}
      <Navbar />

      <div className="home-title">
        <h1>Helping Universe NGO</h1>
        <p>Support children in need. Donate, volunteer, and make a difference today.</p>
      </div>

      <div className="slideshow">
        {slides.map((slide, index) => (
          <img
            key={index}
            src={slide}
            alt={`slide-${index}`}
            className={index === current ? "active" : ""}
          />
        ))}
      </div>

      <div className="home-buttons">
        <button onClick={() => (window.location.href = "/donate-options")}>Donate Now üíñ</button>
        <button onClick={() => (window.location.href = "/activities")}>Our Activities üåç</button>
        <button onClick={() => (window.location.href = "/about")}>About Us üè†</button>
        <button onClick={() => (window.location.href = "/volunteers")}>Our Volunteers üë•</button>
      </div>
    </div>
  );
}

export default Home;
