import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import {
  Heart,
  BookOpen,
  Droplet,
  TreePine,
  Users,
  School,
} from "lucide-react";
import "./Home.css";

function Home() {
  const slides = [
    "/images/food-donation.jpg",
    "/images/education.jpg",
    "/images/children-smile.jpg",
    "/images/medical-help.jpg",
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const activities = [
    {
      icon: <Heart size={40} color="#ff4757" />,
      title: "Health & Medical Camps",
      desc: "We organize free health checkups, blood donation drives, and awareness programs to ensure community well-being.",
      bg: "#fff0f0",
    },
    {
      icon: <BookOpen size={40} color="#1e90ff" />,
      title: "Education for All",
      desc: "We run programs to educate underprivileged children and empower women through skill-based learning.",
      bg: "#f0f8ff",
    },
    {
      icon: <Droplet size={40} color="#00bcd4" />,
      title: "Clean Water Initiative",
      desc: "We provide clean drinking water solutions in rural areas to promote health and hygiene.",
      bg: "#e0f7fa",
    },
    {
      icon: <TreePine size={40} color="#2ecc71" />,
      title: "Environment Protection",
      desc: "We actively plant trees, promote recycling, and spread awareness on climate change.",
      bg: "#eafaf1",
    },
    {
      icon: <Users size={40} color="#ff9f43" />,
      title: "Community Empowerment",
      desc: "Our NGO supports local communities with livelihood programs and financial literacy workshops.",
      bg: "#fff7e6",
    },
    {
      icon: <School size={40} color="#8e44ad" />,
      title: "Child Sponsorship",
      desc: "We ensure every child has access to education, nutrition, and a nurturing environment for growth.",
      bg: "#f5e8ff",
    },
  ];

  return (
    <div className="home-container">
      {/* ‚úÖ Navbar */}
      <nav className="navbar">
        <div className="navbar-logo">üåç Helping Universe NGO</div>
        <div className="navbar-links">
          <a href="/">Home</a>
          <a href="/about">About</a>
          <a href="/donate-options">Donate</a>
          <a href="/activities">Activities</a>
          <a href="/volunteers">Volunteers</a>
          <button
            className="nav-btn"
            onClick={() => (window.location.href = "/login")}
          >
            Login
          </button>
          <button
            className="nav-btn"
            onClick={() => (window.location.href = "/register")}
          >
            Register
          </button>
        </div>
      </nav>

      {/* üéû Slideshow */}
      <div className="slideshow">
        {slides.map((slide, index) => (
          <img
            key={index}
            src={slide}
            alt={`slide-${index}`}
            className={index === current ? "active" : ""}
          />
        ))}
      </div>

      {/* ‚ú® Title */}
      <div className="home-title">
        <h1>Helping Universe NGO</h1>
        <p>
          Support children in need. Donate, volunteer, and make a difference
          today.
        </p>
      </div>

      {/* üíñ Buttons */}
      <div className="home-buttons">
        <button onClick={() => (window.location.href = "/donate-options")}>
          Donate Now üíñ
        </button>
        <button onClick={() => (window.location.href = "/activities")}>
          Our Activities üåç
        </button>
        <button onClick={() => (window.location.href = "/about")}>
          About Us üè†
        </button>
        <button onClick={() => (window.location.href = "/volunteers")}>
          Our Volunteers üë•
        </button>
      </div>

      {/* üéâ Celebration Booking */}
      <div className="celebration-section">
        <h2>üéâ Celebration Booking</h2>
        <p>Celebrate your special moments by bringing smiles to those in need!</p>
        <div className="celebration-buttons">
          <button onClick={() => (window.location.href = "/birthday-booking")}>
            üéÇ Birthday Celebration
          </button>
          <button onClick={() => (window.location.href = "/occasion-booking")}>
            üéä Other Occasion Celebration
          </button>
        </div>
      </div>

      {/* üåü Our Activities Section */}
      <section className="activities-section">
        <h2>üåü Our Activities</h2>
        <p className="section-desc">
          Every small step we take today creates a big impact tomorrow.
        </p>

        <div className="activities-grid">
          {activities.map((act, index) => (
            <motion.div
              key={index}
              className="activity-card"
              style={{ backgroundColor: act.bg }}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.5 }}
            >
              <div className="activity-icon">{act.icon}</div>
              <h3>{act.title}</h3>
              <p>{act.desc}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* üöÄ Call to Action */}
      <section className="cta-section">
        <h2>Be a Part of the Change üåç</h2>
        <p>
          Join hands with us ‚Äî volunteer, donate, or spread awareness. Together,
          we can bring light to someone's life.
        </p>
        <button className="join-btn">Join Our Mission üöÄ</button>
      </section>
    </div>
  );
}

export default Home;
