import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Home.css";

const images = [
  "/images/food-donation.jpg",
  "/images/education.jpg",
  "/images/children-smile.jpg",
  "/images/medical-help.jpg",
];

const Home = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">Helping Universe üåç</div>
        <div className="nav-links">
          <Link to="/login" className="btn">
            Login
          </Link>
          <Link to="/register" className="btn btn-primary">
            Register
          </Link>
        </div>
      </nav>

      {/* Slideshow */}
      <div className="slideshow">
        <img
          src={images[currentIndex]}
          alt="Helping Universe Slide"
          className="slide-image fade"
        />
        <div className="overlay">
          <h1>Welcome to Helping Universe</h1>
          <p>
            Together, we can make a difference! Donate food, clothes, books, or
            support education.
          </p>
          <Link to="/donate" className="donate-btn">
            üíñ Donate Now
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
