  const navigate = useNavigate();

  return (
    <div className="home-container">
      <section className="hero-section">
        <h1>Welcome to Helping Universe</h1>
        <p>Support humanity through donations, care, and compassion.</p>
        <button className="donate-btn" onClick={() => navigate("/donate")}>
          üíñ Donate Now
        </button>
      </section>

      <section className="slideshow">
        <img src="https://source.unsplash.com/1200x400/?charity,help" alt="Helping" />
        <img src="https://source.unsplash.com/1200x400/?donation,children" alt="Donation" />
        <img src="https://source.unsplash.com/1200x400/?food,volunteer" alt="Food" />
      </section>

      <section className="activities">
        <button>üåç Our Activities</button>
        <button>üôå Volunteers</button>
        <button>üìò About Us</button>
        <button>üìû Contact</button>
      </section>
    </div>
  );
}

export default Home;
