import React, { useEffect, useState } from "react";
import "./Home.css";
import Navbar from "../components/Navbar";
import slide1 from "../assets/images/slide1.jpg";
import slide2 from "../assets/images/slide2.jpg";
import slide3 from "../assets/images/slide3.jpg";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const images = [slide1, slide2, slide3];
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="home-page">
      <Navbar />

      {/* Slideshow */}
      <div className="slideshow">
        <img src={images[current]} alt="slide" className="slide-image" />
      </div>

      {/* Donate Now Button */}
      <div className="donate-section">
        <button onClick={() => navigate("/donate-options")} className="donate-btn">
          Donate Now
        </button>
      </div>

      {/* Information Buttons */}
      <div className="info-buttons">
        <button>üåç Our Activities</button>
        <button>‚ÑπÔ∏è About Us</button>
        <button>ü§ù Our Volunteers</button>
        <button>üìû Contact Us</button>
      </div>
    </div>
  );
};

export default Home;
